PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE rising_patterns (
                id INTEGER PRIMARY KEY AUTOINCREMENT,
                pattern_name TEXT NOT NULL,
                description TEXT,
                characteristics TEXT,
                example_stock_code TEXT,
                key_days TEXT,
                key_features TEXT,
                validated_success_rate REAL,
                validation_sample_count INTEGER,
                validation_date TEXT,
                created_at TEXT DEFAULT CURRENT_TIMESTAMP
            );
INSERT INTO rising_patterns VALUES(83,'放量突破涨停型','单日涨幅超过5%，成交量显著放大，强势涨停或接近涨停，随后惯性上涨','[''当日涨幅超过5%，收盘价接近最高价'', ''成交量是平时的3倍以上'', ''上影线极短，下影线较长，多头力量强劲'', ''次日继续放量上涨，形成涨停板效应'']','600234','2025-10-14及后续3天','[''当日大阳线涨幅5.26%，收盘价12.00'', ''成交量2460万股，显著放大'', ''价格从10.93涨至12.60，振幅15.3%'', ''3日累计涨幅12.4%'']',3.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(84,'缩量阴线蓄势型','当日小幅下跌或高开低走收阴，成交量缩量，为后续上涨蓄势','[''当日收阴线，跌幅在1-4%之间'', ''开盘价高于收盘价，有冲高回落迹象'', ''成交量相对温和，无恐慌性抛售'', ''次日开始反转上涨，3日涨幅显著'']','600234','2022-03-08及后续3天','[''当日阴线跌幅4.05%，开9.38收9.00'', ''成交量94.91万股，相对温和'', ''次日小幅反弹至9.05'', ''第3日大涨至9.72，3日涨幅8.0%'']',12.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(85,'低位温和放量型','股价处于相对低位，温和放量上涨，实体较小但连续收阳','[''当日小阳线，涨幅在2-4%之间'', ''成交量温和放大，无暴涨暴跌'', ''收盘价接近当日最高价，上影线短'', ''后续延续上涨趋势，呈现阶梯式上升'']','600234','2024-06-12及后续3天','[''当日小阳线涨幅3.03%，收2.04'', ''成交量413.56万股，温和放量'', ''收盘价等于最高价，强势特征明显'', ''3日连续上涨至2.25，累计涨幅10.3%'']',12.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(86,'巨量换手突破型','成交量暴增，换手率极高，价格大幅拉升，突破前期平台','[''当日成交量暴增，达到8000万股以上'', ''涨幅在3-6%之间，伴随巨大波动'', ''振幅超过8%，说明资金激烈博弈'', ''后续惯性上涨明显，多头占据优势'']','002135','2021-09-01及后续3天','[''当日阳线涨幅3.54%，收11.14'', ''成交量8532.9万股，换手率极高'', ''振幅9.66%，从10.56涨至11.58'', ''次日直接高开至12.26，3日涨幅9.8%'']',15.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(87,'连续小阳线攀升型','连续多日收小阳线，每日涨幅温和，成交量逐步放大，稳健上涨','[''当日小阳线，涨幅在1-3%之间'', ''实体较小，上下影线均较短'', ''成交量稳定或温和递增'', ''3日内连续收阳，累计涨幅可观'']','600234','2024-07-30及后续3天','[''当日小阳线涨幅2.84%，收2.53'', ''成交量144.5万股，稳定放量'', ''次日涨至2.66，第3日涨至2.79'', ''3日连续上涨，累计涨幅10.3%'']',12.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(88,'V型反转启动型','当日大幅低开后强势拉升，形成长下影线，显示强劲支撑和反转','[''开盘价明显低于前日收盘价'', ''当日下影线长度超过实体2倍以上'', ''最终收阳或收平，最低价成为支撑位'', ''次日继续上涨，确认反转有效'']','600234','2024-12-10及后续3天','[''当日低开至5.75，最低跌至5.54'', ''最终收跌至5.54，下影线明显'', ''次日大涨至5.80，涨幅4.69%'', ''第3日继续涨至6.09，3日涨幅9.9%'']',23.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(89,'跳空高开加速型','当日跳空高开，收盘继续上涨，形成向上跳空缺口，加速上涨信号','[''开盘价明显高于前日收盘价'', ''当日涨幅在4-8%之间'', ''收盘价接近或等于最高价'', ''次日继续高开高走，涨势延续'']','600234','2025-01-13及后续3天','[''当日大阳线涨幅4.56%，收5.28'', ''开盘5.05，收盘5.28，强势上攻'', ''次日继续涨至5.54，涨幅4.92%'', ''第3日涨至5.73，3日累计涨幅8.5%'']',8.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(90,'震荡筑底突破型','当日宽幅震荡，最终收涨，成交量放大，突破前期震荡区间','[''当日振幅超过7%，波动剧烈'', ''收盘价位于全天中上部位置'', ''成交量显著放大，达到7000万股以上'', ''突破前期压力位，后续加速上涨'']','600335','2021-11-01及后续3天','[''当日大阳线涨幅5.44%，收6.40'', ''振幅8.15%，从6.01涨至6.50'', ''成交量7718.8万股，巨量突破'', ''3日涨至7.28，累计涨幅13.8%'']',8.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(91,'缩量十字星启动型','当日收十字星或小实体，成交量缩量，次日开始启动上涨行情','[''当日实体极小，开收盘价接近'', ''上下影线相对较短，波动不大'', ''成交量明显缩量，筹码稳定'', ''次日突然放量上涨，形成突破'']','600353','2021-12-15及后续3天','[''当日小阳线涨幅0.25%，收4.03'', ''振幅仅2.0%，波动极小'', ''成交量854.6万股，缩量整理'', ''次日涨至4.10，第3日暴涨至4.51，涨幅11.9%'']',23.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(92,'底部放量长阳型','股价在底部区域收出长阳线，成交量显著放大，标志底部确认','[''当日涨幅在4-7%之间，阳线实体较长'', ''成交量较前期明显放大2倍以上'', ''几乎无上影线，买盘强劲'', ''后续连续上涨，底部形态确立'']','002135','2022-02-15及后续3天','[''当日阳线涨幅1.92%，收9.56'', ''成交量2331.9万股，明显放量'', ''次日大涨至10.52，涨幅10.04%'', ''第3日继续涨至10.78，3日涨幅12.7%'']',8.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(93,'超跌反弹爆发型','股价处于深度超跌状态，突然爆发式反弹，单日或次日涨幅巨大','[''当日涨幅相对温和或微涨'', ''次日突然爆发，单日涨幅超过10%'', ''成交量急剧放大，资金大量涌入'', ''3日累计涨幅达到15%以上'']','600335','2021-01-20及后续3天','[''当日小阳线涨幅0.23%，收4.43'', ''振幅仅2.72%，成交量1363.3万股'', ''次日暴涨至4.88，涨幅10.16%'', ''第3日继续涨至5.37，3日累计涨幅21.0%'']',3.0,100,'2025-12-07','2025-12-06 15:46:35');
INSERT INTO rising_patterns VALUES(94,'平台整理突破型','经过长期横盘整理后，温和突破平台，成交量同步放大','[''当日涨幅在2-4%之间'', ''收盘价突破前期平台压力位'', ''成交量较平台整理期明显放大'', ''后续呈现加速上涨态势'']','600353','2022-07-20及后续3天','[''当日小阳线涨幅0.93%，收6.49'', ''成交量1310.2万股，温和放量'', ''次日涨至6.63，第3日涨至7.02'', ''3日累计涨幅8.2%，突破有效'']',18.0,100,'2025-12-07','2025-12-06 15:46:35');
COMMIT;
